int x=0;
int y=200;

void* task_body(void* arg){
    char* pv = arg;
    printf("Welcome -- %s\n", pv);
    for(int i = x; i < y-1; i++){
        if(array[i] > array[i+1])
        {
            if(Max < array[i]){
                Max = array[i];
            }
        }

        else if(array[i] < array[i+1]){
            if(Min > array[i])
            {
                Min = array[i+1];
            }
        }
    }
    x += 200;
    y += 200;
    printf("Max = %d and Min = %d\n", Max, Min);
}
